// Wishes Section
// Fungsi untuk menampilkan komentar dari database
function displayWishes() {
    const wishesContainer = document.getElementById('wishes');
    wishesContainer.innerHTML = ''; // Kosongkan sebelumnya

    // Fetch data dari backend
    fetch('wishes.php')
        .then(response => response.json())
        .then(data => {
            data.forEach(wish => {
                const wishElement = document.createElement('div');
                wishElement.className = 'wish';
                wishElement.innerHTML = `
                    <div class="wish-name">${wish.name}</div>
                    <div class="wish-comment">${wish.comment}</div>
                `;
                wishesContainer.appendChild(wishElement);
            });
        })
        .catch(error => console.error('Error fetching wishes:', error));
}

// Fungsi untuk menambahkan komentar baru ke daftar
function submitWish() {
    const nameInput = document.getElementById('nameInput');
    const wishInput = document.getElementById('wishInput');
    const wishesContainer = document.getElementById('wishes');

    const name = nameInput.value.trim();
    const wish = wishInput.value.trim();

    if (name && wish) {
        // Membuat elemen wish baru
        const wishElement = document.createElement('div');
        wishElement.className = 'wish';

        // Menambahkan konten wish
        wishElement.innerHTML = `
            <div class="wish-content">
                <strong>${name}</strong>: ${wish}
            </div>
            <div class="wish-time">
                ${getCurrentTime()}
            </div>
        `;

        // Menambahkan wish ke container
        wishesContainer.appendChild(wishElement);

        // Membersihkan input
        nameInput.value = '';
        wishInput.value = '';
    } else {
        alert('Please fill out both fields before submitting.');
    }
}
// Fungsi untuk mendapatkan waktu saat ini dalam format "HH:MM, DD-MM-YYYY"
function getCurrentTime() {
    const now = new Date();
    const hours = String(now.getHours()).padStart(2, '0');
    const minutes = String(now.getMinutes()).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    const month = String(now.getMonth() + 1).padStart(2, '0'); // Bulan dimulai dari 0
    const year = now.getFullYear();
    return `${hours}:${minutes}, ${day}-${month}-${year}`;
}

// Panggil fungsi displayWishes saat halaman dimuat
window.addEventListener('load', function () {
    displayWishes();
});



// Countdown Timer Section
const countdownDate = new Date("December 22, 2024 09:00:00").getTime(); // Set tanggal acara pernikahan

const countdownFunction = setInterval(function () {
    const now = new Date().getTime(); // Mendapatkan waktu sekarang
    const distance = countdownDate - now; // Menghitung selisih waktu

    const days = Math.floor(distance / (1000 * 60 * 60 * 24)); // Menghitung hari
    const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)); // Menghitung jam
    const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60)); // Menghitung menit
    const seconds = Math.floor((distance % (1000 * 60)) / 1000); // Menghitung detik

    document.getElementById("days").innerHTML = days; // Menampilkan hari
    document.getElementById("hours").innerHTML = hours; // Menampilkan jam
    document.getElementById("minutes").innerHTML = minutes; // Menampilkan menit
    document.getElementById("seconds").innerHTML = seconds; // Menampilkan detik

    // Jika acara sudah dimulai
    if (distance < 0) {
        clearInterval(countdownFunction);
        document.getElementById("countdown").innerHTML = "Event Started!"; // Menampilkan pesan acara sudah dimulai
    }
}, 1000);


