const toggleButton = document.getElementById("toggleButton");
const contents = [
    document.getElementById("moreContent1"),
    document.getElementById("moreContent2"),
    document.getElementById("moreContent3"),
];
let currentStep = 0;

toggleButton.addEventListener("click", () => {
    if (currentStep < contents.length) {
        // Tampilkan bagian berikutnya
        contents[currentStep].classList.remove("hidden");
        currentStep++;

        // Ubah teks tombol jika masih ada bagian yang tersisa
        if (currentStep === contents.length) {
            toggleButton.textContent = "Lihat Lebih Sedikit Kisah Kami";
        }
    } else {
        // Sembunyikan semua bagian jika sudah mencapai akhir
        contents.forEach(content => content.classList.add("hidden"));
        currentStep = 0;
        toggleButton.textContent = "Lihat Lebih Banyak Kisah Kami";
    }
});
